using System.Collections; using System.Collections.Generic; using UnityEngine; using TMPro; public class Alert : MonoBehaviour {     public TMP_Text alert;      // Start is called before the first frame update     void Start()     {          alert.GetComponent<TMP_Text>();         StartCoroutine(change());         }      // Update is called once per frame     IEnumerator change()     {                 alert.text = "Good Luck";         yield return new WaitForSeconds(1);         alert.fontSize = 15;         alert.text = "use arrows to move and z to attack";              } }